import React from 'react';

function Itinerary({ itinerary, hasData }) {
  if (!hasData) {
    return (
      <div className="itinerary-container">
        <p className="placeholder">Loading data from server...</p>
      </div>
    );
  }

  if (!itinerary) {
    return (
      <div className="itinerary-container">
        <p className="placeholder">
          Enter your details on the left and click <strong>Generate itinerary</strong> to see recommended stops.
        </p>
      </div>
    );
  }

  if (itinerary.stops.length === 0) {
    return (
      <div className="itinerary-container">
        <p className="placeholder">
          No valid itinerary within these constraints. Try increasing budget or time, or relaxing preferences.
        </p>
      </div>
    );
  }

  let cumulativeCost = 0;
  let cumulativeMinutes = 0;

  return (
    <div className="itinerary-container">
      {itinerary.stops.map((stop, idx) => {
        const { place, costBreakdown, timeBreakdown } = stop;
        cumulativeCost += costBreakdown.total;
        cumulativeMinutes += timeBreakdown.total;

        return (
          <article key={place.id} className="itinerary-stop">
            <div className="stop-number">{idx + 1}</div>
            <div className="stop-main">
              <div className="stop-title-row">
                <div className="stop-name">{place.name}</div>
                <div className="stop-chip-row">
                  <span className="chip rating">★ {place.rating.toFixed(1)}</span>
                  <span className="chip neighbourhood">{place.neighbourhood}</span>
                  <span className="chip category">{place.category}</span>
                  <span className="chip cost">KSH {costBreakdown.total}</span>
                </div>
              </div>
              <div className="stop-subrow">
                <span><strong>Transport:</strong> {stop.transport.mode} · KSH {costBreakdown.transport} · {timeBreakdown.travel} min</span>
                <span><strong>Entry:</strong> KSH {costBreakdown.entry}</span>
                <span><strong>Food est.:</strong> KSH {costBreakdown.food}</span>
              </div>
              <div className="stop-subrow">
                <span><strong>Visit duration:</strong> {timeBreakdown.visit} min</span>
                <span><strong>Step total time:</strong> {timeBreakdown.total} min</span>
                <span><strong>Running total:</strong> KSH {cumulativeCost} · {cumulativeMinutes} min</span>
              </div>
            </div>
          </article>
        );
      })}
    </div>
  );
}

export default Itinerary;
