import React from 'react';

function BudgetSummary({ itinerary, formData }) {
  if (!itinerary) {
    return (
      <div className="summary-container">
        <div className="summary-row">
          <span className="summary-label">Total spent</span>
          <span className="summary-value summary-weak">KSH 0</span>
        </div>
        <div className="summary-row">
          <span className="summary-label">Time used</span>
          <span className="summary-value summary-weak">0 min</span>
        </div>
      </div>
    );
  }

  const { stops, remainingBudget, remainingMinutes } = itinerary;

  if (stops.length === 0) {
    return (
      <div className="summary-container">
        <div className="summary-row">
          <span className="summary-label">Total spent</span>
          <span className="summary-value summary-weak">KSH 0</span>
        </div>
        <div className="summary-row">
          <span className="summary-label">Time used</span>
          <span className="summary-value summary-weak">0 min</span>
        </div>
      </div>
    );
  }

  let cumulativeCost = 0;
  let cumulativeMinutes = 0;
  let transportTotal = 0;
  let placeTotal = 0;

  stops.forEach((stop) => {
    cumulativeCost += stop.costBreakdown.total;
    cumulativeMinutes += stop.timeBreakdown.total;
    transportTotal += stop.costBreakdown.transport;
    placeTotal += stop.costBreakdown.entry + stop.costBreakdown.food;
  });

  const budgetUsedPct = (cumulativeCost / formData.budget) * 100;
  const timeUsedPct = (cumulativeMinutes / (formData.hours * 60)) * 100;

  return (
    <div className="summary-container">
      <div className="summary-row">
        <span className="summary-label">Stops visited</span>
        <span className="summary-value">{stops.length}</span>
      </div>
      <div className="summary-row">
        <span className="summary-label">Total on places</span>
        <span className="summary-value">KSH {placeTotal}</span>
      </div>
      <div className="summary-row">
        <span className="summary-label">Total on transport</span>
        <span className="summary-value">KSH {transportTotal}</span>
      </div>
      <div className="summary-row">
        <span className="summary-label">Total spent</span>
        <span className="summary-value summary-strong">KSH {cumulativeCost}</span>
      </div>
      <div className="summary-row">
        <span className="summary-label">Budget remaining</span>
        <span className="summary-value summary-strong">KSH {formData.budget - cumulativeCost}</span>
      </div>
      <hr style={{ borderColor: 'rgba(31,41,55,0.9)', margin: '0.35rem 0' }} />
      <div className="summary-row">
        <span className="summary-label">Time used</span>
        <span className="summary-value">{cumulativeMinutes} min ({timeUsedPct.toFixed(0)}% of budgeted time)</span>
      </div>
      <div className="summary-row">
        <span className="summary-label">Time remaining</span>
        <span className="summary-value">{Math.max(0, Math.round(remainingMinutes))} min</span>
      </div>
      <div className="summary-row">
        <span className="summary-label">Budget used</span>
        <span className="summary-value">{budgetUsedPct.toFixed(0)}% of KSH {formData.budget}</span>
      </div>
    </div>
  );
}

export default BudgetSummary;
